var cluster = require("cluster");
module.exports = function(app){
    var result = [];
    for(var p in cluster.workers){
        result.push({
            uniqueID:cluster.workers[p]['uniqueID'],
            workerID:cluster.workers[p]['workerID'],
            state:cluster.workers[p]['state']
        });
    }
    app.send(JSON.stringify(result));
};