var path = require('path');

//所有模块的入口文件
exports.SERVER = 'HARBORS/0.3.4';
exports.DEBUG = false;
exports.BASEDIR = path.join(__dirname,'../../../');//basic dir
exports.DEBUG ?
    exports.log = function(){
        var str;
        for(var p in arguments){
            if(!str) str = arguments[p]
            else str += arguments[p];
        }
        console.log(str);
    } :
    exports.log = function(){};
