/**
 * -
 * @param module
 * @returns {number}
 */
exports.getvhost = function(module){
    return module & 0x1;
};
exports.vhost = function(module){
    return module | 0x1;
};

exports.getcache = function(module){
    return module >> 1 & 0x1;
};
exports.cache = function(module){
    return module | 0x2;
};

exports.getzip = function(module){
    return module >> 2 & 0x1;
};
exports.zip = function(module){
    return module | 0x4;
};

exports.getrewrite = function(module){
    return module >> 3 & 0x1;
};
exports.rewrite = function(module){
    return module | 0x8;
};

exports.getnodejs = function(module){
    return module >> 4 & 0x1;
};
exports.nodejs = function(module){
    return module | 0x10;
};

exports.getphp = function(module){
    return module >> 5 & 0x1;
};
exports.php = function(module){
    return module | 0x20;
};
