var harbors = require('../harbors');
var multiparty = require('multiparty');
exports.handle = function(object, next, skip){
    harbors.log("Begin to receive post...");
    if(object.client.method == "post"){
        var form = new multiparty.Form({
            uploadDir:object._config.tmp_dir
        });
        form.parse(object.request, next);
    }else{
        skip();
    }
};
