/**
 * 设置cookie
 * @param cookie
 * @param req
 * @param res
 */
exports.setCookie = function(cookie,req,res){
    var result = [];
    for(var p in cookie){
        result.push(p+"="+cookie[p]);
    }
    res.setHeader("Set-Cookie", result);
};

/**
 * 删除cookie
 * @param cookie
 * @param req
 * @param res
 */
exports.delCookie = function(cookie,req,res){
    var result = [];
    for(var p in cookie){
        result.push(p+"="+cookie[p]+";expires="+new Date()+";");
    }
    this.res.setHeader("Set-Cookie", result);
};