
var header = function(obj){
    this.res.setHeader("Server", 'Node Harbors');
    this.res.__cookie && this.res.setHeader("Set-cookie", this.res.__cookie||[]);
    obj && obj.type &&this.res.setHeader('Content-Type', obj.type);
    obj && obj.lm && this.res.setHeader("Last-Modified", obj.lm);
    obj && obj.length && this.res.setHeader('Content-Length',obj.length);
    obj && obj.st && this.res.setHeader('Cache-Control','max-age=' + obj.st);
    obj && obj.expires && this.res.setHeader('Expires',obj.expires.toUTCString());
};

module.exports = {
    '304':function(obj){
        header.call(this,obj);
        this.res.writeHead(304, "Not Modified");
    },
    '404':function(obj){
        header.call(this,obj);
        this.res.writeHead(404, "Not found T.T");
        this.res.end('Sorry, url is not found.');
    },
    '200':function(obj){
        header.call(this,obj);
        this.res.writeHead(200, "OK ^_^");
    },
    '500':function(obj){
        header.call(this,obj);
        this.res.writeHead(500, 'Server error T.T');
        this.res.end('Sorry, url is not found.');
    }
};