var mysql = require('mysql');

var db;
function handleError (err) {
    if (err) {
        // 如果是连接断开，自动重新连接
        if (err.code === 'PROTOCOL_CONNECTION_LOST') {
            connect();
        } else {
            console.error(err.stack || err);
        }
    }
}

// 连接数据库
function connect (config) {
    db = mysql.createConnection({
        host:config.mysql.Host,
        user:config.mysql.User,
        password:config.mysql.Password
    });
    db.connect();
    db.on('error', handleError);
    db.query('use '+config.mysql.Database);
}


//db.query('use itharbors');
//db.query('select * from users',function(err,res,fil){
//    harbors.log(res);
//})

exports.mysql = function(query,callback){
    db.query(query,function(error, results, fields){
        typeof(callback)=='function'&&callback(error, results, fields);
    });
};
exports.init = function(config){
    connect(config);
    return exports.mysql;
};
