var path = require('path');
var app = {
    route:function(req){
        var route = req.url;//格式化请求地址，如/admin/index
        return route.split("/").splice(1);//[ 'news', 'index' ]
    },
    Get:function(req){
        var temp = req.url.split("?");
        if(temp[1]){
            var Get = {};
            var temp = temp[1].split("&");
            for(var i=0;i<temp.length;i++){
                var temp_2 = temp[i].split("=");
                Get[temp_2[0]] = temp_2[1];
            }
            return Get;
        }else{return {}}//{ asdfwe: 'asdf' }
    }
};
module.exports = app;