var debug = require('./debug');
module.exports = {
    h304:function(res){
        res._testStatus = '304';
        res.setHeader("Server", 'Harbors');
        res.writeHead(304, "Not Modified");
        res.end();
        debug.endTest(res);
    },
    h404:function(res){
        res._testStatus = '404';
        res.setHeader("Server", 'Harbors');
        res.setHeader("Last-Modified", 0);
        res.writeHead(404, "Not found");
        res.end('Sorry, url is not found.');
        debug.endTest(res);
    },
    h500:function(res){
        res._testStatus = '500';
        res.setHeader("Server", 'Harbors');
        res.setHeader("Last-Modified", 0);
        res.setHeader('Content-Type', 'text/plain');
        res.writeHead(500, 'Server error ');
        res.end('Sorry, the server error encountered a temporary error, please try to refresh.');
        debug.endTest(res);
    },
    h200:function(res,status){
        res._testStatus = status||'200';
        res.setHeader("Server", 'Harbors');
        res.writeHead(200, 'Ok');
        debug.endTest(res);
    }
};
