var cluster = require('cluster');

//创建子进程
exports.createWorker = function(){
    harbors.log('harbors Service process started,Listening port:'+harbors.config.listenPort+'.Open the process number:'+harbors.config.clusterNum);
    for(var i=0;i<harbors.config.clusterNum;i++){
        Worker();
    }
};
var Worker = function(){//创建工作进程
    var cp = cluster.fork();
    harbors.log('The promoter of new process. clusterid:'+cp.workerID+'. pid:'+cp.process.pid);
    cp.on('exit', function(code, signal) {
        harbors.log('The child process an unexpected error. Code:'+code+'.');
        //重启worker（此时进程id会增加1，以示和原错误关闭进程区别）
        harbors.log('Separation of new sub process process...');
        setTimeout(Worker,2000);
    });
};

