/**
 * 开发模式
 */
var dev = function(){
    harbors._dev = true;
};

/**
 * 初始化项目，新建各种文件夹等
 */
var init = function(){
    console.log("init  project...");
};

/**
 * 整理参数
 */
var param = process.argv.splice(2);
if(param[0]){
    for(var i=0;i<param.length;i++){
        switch(param[i]){
            case '--dev':
                dev();
                break;
            case '--init':
                init();
                break;
        }
    }
}
